// Static blur placeholders for images to improve perceived loading performance
// These are small, base64-encoded placeholders that show while images load

// Generic blur placeholder for landscape images
export const LANDSCAPE_BLUR_PLACEHOLDER = 
  "data:image/jpeg;base64,"

// Portrait/square blur placeholder for profile images
export const PORTRAIT_BLUR_PLACEHOLDER = 
  "data:image/jpeg;base64,"

// Dark blur placeholder for hero/banner images with overlay
export const DARK_BLUR_PLACEHOLDER = 
  "data:image/jpeg;base64,"

// Utility function to get appropriate blur placeholder based on image type
export function getBlurPlaceholder(imageType: 'landscape' | 'portrait' | 'hero' = 'landscape'): string {
  switch (imageType) {
    case 'portrait':
      return PORTRAIT_BLUR_PLACEHOLDER
    case 'hero':
      return DARK_BLUR_PLACEHOLDER
    case 'landscape':
    default:
      return LANDSCAPE_BLUR_PLACEHOLDER
  }
} 